// Simple test server to verify splash logic\nconst http = require('http');\nconst fs = require('fs');\nconst path = require('path');\n\nconst server = http.createServer((req, res) => {\n  if (req.url === '/' || req.url === '/index.html') {\n    fs.readFile('index.html', (err, data) => {\n      if (err) {\n        res.writeHead(404);\n        res.end('File not found');\n      } else {\n        res.writeHead(200, {'Content-Type': 'text/html'});\n        res.end(data);\n      }\n    });\n  } else {\n    res.writeHead(404);\n    res.end('Not found');\n  }\n});\n\nconst PORT = 3000;\nserver.listen(PORT, () => {\n  console.log(`\\nSplash test server running at: http://localhost:${PORT}`);\n  console.log('\\nTest checklist:');\n  console.log('✅ Page loads with hero splash visible');\n  console.log('✅ Scroll/touch exits splash within 1.5s');\n  console.log('✅ Content becomes scrollable after exit');\n  console.log('✅ Timeout auto-exits after 1.5s if no interaction');\n  console.log('\\nPress Ctrl+C to stop');\n});